mapper.select:
SELECT id, userId, name, gender, proPhoto
FROM usertbl
WHERE userId=#{userId} and pw=#{pw}

SELECT id, userId, name, gender, proPhoto
FROM usertbl
WHERE userId=#{userId}

SELECT id, userId, name, gender, proPhoto
FROM usertbl
WHERE id=#{id}

mapper.update:
UPDATE usertbl
proPhoto = #{proPhoto}, name = #{name}, pw = #{pw},
WHERE id=#{id}
